module.exports = function() {
  var $play = document.getElementById('wistia-play')
  if (!$play) return

  var player

  function showPlay() { $play.style.display = 'block'; }
  function hidePlay() { $play.style.display = 'none'; }

  $play.addEventListener('click', function() {
    hidePlay();
    player.play();
  })

  function onWistiaReady(video) {
    player = video
    player.bind('pause', showPlay);
    player.bind('end', showPlay);
    player.bind('play', hidePlay);
  }

  window._wq = window._wq || [];
  window._wq.push({
    id: window.wistiaID,
    onReady: onWistiaReady,
  })
}
